mapper.select:
/*kr.happyjob.study.hwang.dao.HnoticeDao.hnoticelist*/ select noti.notice_no ,noti.loginID ,ui.name as writer ,noti.notice_title ,noti.notice_date ,noti.notice_det ,noti.file_name ,noti.file_size ,noti.file_nadd ,noti.file_madd from tb_notice noti inner join tb_userinfo ui on ui.loginID = noti.loginID
and noti.notice_title Like CONCAT('%', #{scrtitle}, '%')
and noti.notice_date >= #{srcsdate}
and noti.notice_date <= #{srcedate}
ORDER BY noti.notice_date DESC LIMIT #{pageindex}, #{pageSize}

		/*kr.happyjob.study.hwang.dao.HnoticeDao.counthnoticelist*/
		SELECT COUNT(1) AS tot_cnt 
         from tb_notice     


                    and notice_title Like CONCAT('%', #{scrtitle}, '%')


                   and notice_date  >= #{srcsdate}


                  and notice_date <= #{srcedate}

mapper.insert:

            select ifnull(max(notice_no),0) + 1 from tb_notice

	     insert into tb_notice 
	                    ( 
	                       notice_no
                          ,loginID
                          ,notice_title
                          ,notice_det
                          ,notice_date
                          ,file_name
                          ,file_size
                          ,file_nadd
                          ,file_madd 
	                    ) values (
	                      #{noticeno}  
	                     , #{loginId}      

	                     , #{notice_title}   
	                     , #{notice_det}


	                     , #{notice_titlefile}   
	                     , #{notice_detfile}

	                     , DATE_FORMAT(now(), '%Y-%m-%d')	                       
	                     , #{fileInfo.file_nm}   
	                     , #{fileInfo.file_size}
	                     , #{fileInfo.file_nadd}  
	                     , #{fileInfo.file_loc} 
	                    )

mapper.update:
	      update tb_notice
	           set loginID = #{loginId}  	           

                   ,notice_title = #{notice_title}
                   ,notice_det = #{notice_det} 


	                ,notice_title = #{notice_titlefile}   
	               ,notice_det = #{notice_detfile}

                   ,notice_date = DATE_FORMAT(now(), '%Y-%m-%d')
                   ,file_name = #{fileInfo.file_nm} 
                   ,file_size = #{fileInfo.file_size}
                   ,file_nadd = #{fileInfo.file_nadd}  
                   ,file_madd  = #{fileInfo.file_loc} 
	       where notice_no = #{notice_no}
